<?php class ModelPaymentYeepay extends Model {  	public function getMethod($address) {		$this->load->language('payment/yeepay');		if ($this->config->get('yeepay_status')) {			$status = TRUE;		} else {			$status = FALSE;		}		$method_data = array();		if ($status) {  			$method_data = array( 				'code'         => 'yeepay',				'title'      => $this->language->get('text_title'),				'sort_order' => $this->config->get('yeepay_sort_order')			);		}		return $method_data;	}}?>